Properties.Octopus.Action.Script.ScriptBody:
# Check if SQL Release is installed.
$sqlReleaseModule = Get-Module -ListAvailable -Name SQLRelease
if ($sqlReleaseModule -eq $null) { 
    throw "Cannot find SQL Release on your Octopus Tentacle. If SQL Release is installed, try restarting the Tentacle service for it to be detected."
}

# Check the parameters.
if (-NOT $SQLReleaseDatabaseName) { throw "You must enter a value for 'Target database name'." }
if (-NOT $SQLReleaseDatabaseServer) { throw "You must enter a value for 'Target SQL Server instance'." } 
if (-NOT $SQLReleaseNuGetDbPackageDownloadStepName) { throw "You must enter a value for 'Database package step'." } 

# Check whether database deployment resources export path exists and is a valid directory path 
if((Test-Path $SQLReleaseDeploymentResourcesPath) -eq $true) {
    if((Get-Item $SQLReleaseDeploymentResourcesPath) -isnot [System.IO.DirectoryInfo]) {
        throw "The export path is not a valid folder: $SQLReleaseDeploymentResourcesPath"
    }
} else {
    throw "The export path folder doesn't exist, or the current Windows account can't access it: $SQLReleaseDeploymentResourcesPath"
}

# Get the NuGet package installation directory path.
$packagePath = $OctopusParameters["Octopus.Action[$SQLReleaseNuGetDbPackageDownloadStepName].Output.Package.InstallationDirectoryPath"]
if($packagePath -eq $null) {
    throw "The 'Database package download step' is not a 'Deploy a NuGet package' step: '$SQLReleaseNuGetDbPackageDownloadStepName'"
}

# Work out where the scripts folder from the downloaded NuGet package is.
$databaseStatePath = Join-Path -Path $packagePath -ChildPath "db\state" 

# Constructing the unique export path.
$projectId = $OctopusParameters["Octopus.Project.Id"]
$releaseNumber = $OctopusParameters["Octopus.Release.Number"]
$nugetPackageId = $OctopusParameters["Octopus.Action[$SQLReleaseNuGetDbPackageDownloadStepName].Package.NuGetPackageId"]
$exportPath = Join-Path (Join-Path (Join-Path $SQLReleaseDeploymentResourcesPath $projectId) $releaseNumber) $nugetPackageId

# Make sure the directory we're about to create doesn't already exist, and delete any files if requested.
if ((Test-Path $exportPath) -AND ((Get-ChildItem $exportPath | Measure-Object).Count -ne 0)) {
    if ($SQLReleaseDeleteExistingFiles -eq 'True') {
        Write-Host "Deleting all files in $exportPath"
        rmdir $exportPath -Recurse -Force
    } else {
        throw "The export path is not empty: $exportPath.  Select the 'Delete files in export folder' option to overwrite the existing folder contents."
    }
}

$targetDB = New-DatabaseConnection -ServerInstance $SQLReleaseDatabaseServer -Database $SQLReleaseDatabaseName -Username $SQLReleaseDatabaseUsername -Password $SQLReleaseDatabasePassword | Test-DatabaseConnection
$ignoreStaticData = $SQLReleaseIgnoreStaticData -eq "True"

# Create the deployment resources from the database to the NuGet package
if ($SQLReleaseFilterPath) {
    if ($SQLReleaseCompareOptions) {
        $release = New-DatabaseRelease -Target $targetDB -Source $databaseStatePath -TransactionIsolationLevel $SQLReleaseTransactionIsolationLevel -IgnoreStaticData:$ignoreStaticData -FilterPath $SQLReleaseFilterPath -SQLCompareOptions $SQLReleaseCompareOptions -Verbose
    } else {
        $release = New-DatabaseRelease -Target $targetDB -Source $databaseStatePath -TransactionIsolationLevel $SQLReleaseTransactionIsolationLevel -IgnoreStaticData:$ignoreStaticData -FilterPath $SQLReleaseFilterPath -Verbose
    }
} else {
    if ($SQLReleaseCompareOptions) {
        $release = New-DatabaseRelease -Target $targetDB -Source $databaseStatePath -TransactionIsolationLevel $SQLReleaseTransactionIsolationLevel -IgnoreStaticData:$ignoreStaticData -SQLCompareOptions $SQLReleaseCompareOptions -Verbose
    } else {
        $release = New-DatabaseRelease -Target $targetDB -Source $databaseStatePath -TransactionIsolationLevel $SQLReleaseTransactionIsolationLevel -IgnoreStaticData:$ignoreStaticData -Verbose
    }
}

# Export the deployment resources to disk
$release | Export-DatabaseRelease -Path $exportPath -Verbose
        
# Import the changes summary, deployment warnings, and update script as Octopus artifacts, so you can review them.
New-OctopusArtifact "$exportPath\Reports\Changes.html"
New-OctopusArtifact "$exportPath\Reports\Warnings.xml"
New-OctopusArtifact "$exportPath\Update.sql"
